        return new Source("{% extends 'base.html.twig' %}

{% block title %}Mon compte - e-commerce{% endblock %}

{% block body %}
    <h2>Mon compte</h2>
    Bienvenue dans votre espace membre {{ app.user.firstname }}.
    <hr>
    {% for message in app.flashes('notice') %}
        <div class=\"alert alert-success\">
            {{ message }}
        </div>
    {% endfor %}
    <div class=\"list-group\">
        {% if is_granted('ROLE_ADMIN') %}
        <a href=\"{{ path('admin') }}\" class=\"list-group-item list-group-item-action\">
            Back office
        </a> 
        {% endif %}
        <a href=\"{{ path('account_orders') }}\" class=\"list-group-item list-group-item-action\">
            Mes commandes
        </a>
        <a href=\"{{ path('account_address') }}\" class=\"list-group-item list-group-item-action\">
            Gérer mes adresses
        </a>
        <a href=\"{{ path('account_password') }}\" class=\"list-group-item list-group-item-action\">
            Modifier mot de passe
        </a>
    </div>


{% endblock %}
", "account/index.html.twig", "C:\\laragon\\www\\ecommerce\\templates\\account\\index.html.twig");
